import { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

const schools = [
  {
    id: "neu",
    title: "Northeastern University — MS in Computer Science",
    subtitle: "Boston/Seattle • Sep 2024 – Dec 2026 • GPA 4.0/4.0",
    details: [
      "Relevant coursework: OOP, Data Structures & Algorithms, Web + Mobile Development, Advanced Software Development, DBMS, Cloud Computing, NLP",
      "Activities: UbiWell Lab — personal health informatics & HCI."
    ]
  },
  {
    id: "fandm",
    title: "Franklin & Marshall College — BA in Business, Administration & Management",
    subtitle: "Lancaster, PA • Aug 2018 – Dec 2021 • GPA 3.72/4.0",
    details: [
      "Foundations in analytics, management, and communication supporting cross‑functional engineering work."
    ]
  }
];

export const Education = () => {
  const [openId, setOpenId] = useState("neu");

  return (
    <section className="education" id="education">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <h2>Education</h2>
                  <p>Tap a card to expand details.</p>
                  <div className="edu-grid">
                    {schools.map(s => (
                      <div
                        key={s.id}
                        className={"edu-card " + (openId === s.id ? "open" : "")}
                        onClick={() => setOpenId(openId === s.id ? "" : s.id)}
                        role="button"
                        tabIndex={0}
                        onKeyPress={() => setOpenId(openId === s.id ? "" : s.id)}
                      >
                        <div className="edu-head">
                          <h4>{s.title}</h4>
                          <span className="edu-sub">{s.subtitle}</span>
                        </div>
                        {openId === s.id && (
                          <ul className="edu-body">
                            {s.details.map((d, i) => <li key={i}>{d}</li>)}
                          </ul>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
